/**
 * Edit form callback.
 */
function $module$_$plugin_owner$_$plugin_type$_$plugin_name_short$_edit_form($form, &$form_state) {
  $defaults = array();
  if (isset($form_state['subtype']['defaults'])) {
    $defaults = $form_state['subtype']['defaults'];
  }
  elseif (isset($form_state['plugin']['defaults'])) {
    $defaults = $form_state['plugin']['defaults'];
  }

  $conf = $form_state['conf'] + $defaults;

  $key = 'foo';
  $form[$key] = array(
    '#type' => 'textfield',
    '#title' => t('Foo'),
    '#default_value' => $conf[$key],
  );

  return $form;
}

/**
 * Edit form validate callback.
 */
/*
function $module$_$plugin_owner$_$plugin_type$_$plugin_name_short$_edit_form_validate($form, &$form_state) {
}
*/

/**
 * Edit form submit callback.
 */
function $module$_$plugin_owner$_$plugin_type$_$plugin_name_short$_edit_form_submit($form, &$form_state) {
  $defaults = array();
  if (isset($form_state['subtype']['defaults'])) {
    $defaults = $form_state['subtype']['defaults'];
  }
  elseif (isset($form_state['plugin']['defaults'])) {
    $defaults = $form_state['plugin']['defaults'];
  }

  foreach (array_keys($defaults) as $key) {
    $form_state['conf'][$key] = $form_state['values'][$key];
  }
}

/**
 * Admin info callback.
 *
 * @param string $subtype
 *   Identifier of subtype.
 * @param array $conf
 *   Pane configuration. Array keys are depends on the _edit_form.
 *   - override_title
 *   - override_title_text
 * @param array $context
 *   An array of context objects available for use. These may be placeholders.
 *
 * @return object
 *   Administrative title and description of the $subtype pane.
 *   The keys are:
 *   - title: string
 *   - content: string|array
 */
function $module$_$plugin_owner$_$plugin_type$_$plugin_name_short$_admin_info($subtype, $conf, $context) {
  $return = new stdClass();
  $return->title = 'Foo';
  $return->content = "This is the admin info of $module$:$plugin_name_short$:$subtype";

  return $return;
}

/**
 * Render callback. 
 *
 * "$plugin_owner_human$:$plugin_type_human$" plugin render callback for "$module_human$:$plugin_name_short_human$".
 *
 * @param string $subtype
 *   Subtype identifier.
 * @param array  $conf
 *   Configuration of the $subtype instance.
 * @param array  $args
 *   Documentation missing.
 * @param array  $pane_context
 *   Documentation missing.
 * @param array  $incoming_content
 *   Documentation missing.
 *
 * @return object
 *   The content.
 */
function $module$_$plugin_owner$_$plugin_type$_$plugin_name_short$_render($subtype, $conf, $args, $pane_context, $incoming_content) {
  $block = new stdClass();
  $block->module = '$module$';
  $block->delta = $subtype;
  $block->title = '@todo';
  $block->content = array(
    'content' => array(
      '#type' => 'markup',
      '#markup' => "<div>This is the content of $module$:$plugin_name_short$:$subtype</div>",
    ),
  );

  return $block;
}

