/**
 * Implements hook_ctools_plugin_api().
 */
function $module$_ctools_plugin_api($owner, $api) {
  switch ("$owner:$api") {
    case 'hook_ctools_plugin_api_item_v1$END$':
      return array('version' => '1');

    case 'hook_ctools_plugin_api_item_v2':
      return array('version' => '2');

    case 'hook_ctools_plugin_api_item_v3':
      return array('version' => '3');

  }

  return NULL;
}

/**
 * Implements hook_ctools_plugin_directory().
 */
function $module$_ctools_plugin_directory($owner, $plugin_type) {
  switch ("$owner:$plugin_type") {
    case '$owner_plugin_type$':
      return "plugins/$owner/$plugin_type";
  }

  return NULL;
}

