/**
 * Implements hook_date_views_fields().
 */
function $module$_date_views_fields($field) {
  $return = array();
  switch ($field) {
    case '$tableName$.$fieldName$':$END$
      $return['sql_type'] = (defined('DATE_DATETIME') ? DATE_DATETIME : 'datetime');
      $return['is_field'] = FALSE;
      break;

  }

  return $return;
}

