/**
 * Implements hook_field_extra_fields().
 */
function $module$_field_extra_fields() {
  $return = array();

  $entity_type = '$entity_type$';
  $entity_info = entity_get_info($entity_type);
  foreach (array_keys($entity_info['bundles']) as $bundle) {
    $return[$entity_type][$bundle]['form']['$module$_$field$'] = array(
      'label' => t('$module_human$: $label$'),
      'description' => t('$description$'),
      'weight' => $weight$,
    );

    $return[$entity_type][$bundle]['display']['$module$_$field$'] = array(
      'label' => t('$module_human$: $label$'),
      'description' => t('$description$'),
      'weight' => $weight$,
    );
  }

  return $return;
}

/**
 * Implements hook_field_extra_fields_alter().
 */
function $module$_field_extra_fields_alter(&$info) {
  $entity_type = '$entity_type$';
  if (isset($info[$entity_type])) {
    $entity_info = entity_get_info($entity_type);
    foreach (array_keys($entity_info['bundles']) as $bundle) {
      if (isset($info[$entity_type][$bundle]['form']['$field$'])) {
        $info[$entity_type][$bundle]['form']['$field$']$END$;
      }

      if (isset($info[$entity_type][$bundle]['display']['$field$'])) {
        $info[$entity_type][$bundle]['form']['$field$'];
      }
    }
  }
}

