/**
 * Implements hook_field_formatter_prepare_view().
 */
function $module$_field_formatter_prepare_view($entity_type, $entities, $field, $instances, $langcode, &$items, $displays) {
  switch ($field['type']) {
    case 'taxonomy_term_reference':
      taxonomy_field_formatter_prepare_view($entity_type, $entities, $field, $instances, $langcode, $items, $displays);
      break;

    case 'entityreference':
      entityreference_field_formatter_prepare_view($entity_type, $entities, $field, $instances, $langcode, $items, $displays);
      break;

    case 'user_reference':
      user_reference_field_formatter_prepare_view($entity_type, $entities, $field, $instances, $langcode, $items, $displays);
      break;

    case 'node_reference':
      node_reference_field_formatter_prepare_view($entity_type, $entities, $field, $instances, $langcode, $items, $displays);
      break;

  }
}

