/**
 * Implements hook_rules_file_info().
 */
function $module$_rules_file_info() {
  $components = array(
    $END$//'action',
    //'condition',
    //'data',
    //'event',
    //'plugin',
    //'evaluator',
    //'data_processor',
  );

  $path = drupal_get_path('module', '$module$');
  $info = array();
  foreach ($components as $component) {
    $dir = "includes/rules/$component";
    $files = file_scan_directory("$path/$dir", '/.+\.inc$/', array('recurse' => FALSE));
    foreach ($files as $file) {
      $info[] = "$dir/{$file->name}";
    }
  }

  return $info;
}

