/**
 * Implements hook_tokens().
 */
function $module$_tokens($type, $tokens, array $data = array(), array $options = array()) {
  $return = array();
  if ($type == '$type$' && !empty($data['$data$'])) {
    foreach ($tokens as $name => $original) {
      switch ($name) {
        case '$token$':
          $return[$original] = $data['$data$']$END$;
          break;

      }
    }
  }

  return $return;
}

