/**
 * Implements hook_views_default_views().
 */
function $module$_views_default_views() {
  $files = file_scan_directory(
    drupal_get_path('module', '$module$') . '/includes/views/default',
    '/.+\.inc$/',
    array('recurse' => FALSE)
  );

  $return = array();
  foreach ($files as $file) {
    $view = NULL;
    include $file->uri;
    if ($view) {
      $return[$view->name] = $view;
    }
  }

  return $return;
}

