/**
 * Implements hook_strongarm().
 */
function $module$_strongarm() {
  $return = array();

  $name_value = array(
    '$END$' => 'foo',
  );

  foreach ($name_value as $name => $value) {
    $return[$name] = (object) array(
      'disabled' => FALSE,
      'api_version' => 1,
      'name' => $name,
      'value' => $value,
    );
  }

  return $return;
  // features[ctools][] = strongarm:strongarm:1
  // features[variable][] = variable_name
}

