/**
 * Settings form of "$module_human$: $plugin_name_short_human$" panel style in region context.
 *
 * @param array $settings
 *   Saved settings of this style or an empty array.
 * @param panels_display $panels_display
 *   Panels display object.
 * @param string $region_id
 *   Machine name of the region in the layout.
 * @param string $box_type
 *   The value can be 'display' or 'region'.
 * @param array $form_state
 *   Form API form state array.
 *
 * @return array
 *   Form API render array.
 */
function $module$_$plugin_owner$_$plugin_type$_$plugin_name_short$_region_settings_form($settings, $panels_display, $region_id, $box_type, &$form_state) {
  // This is just an example.
  $key = 'pane_separator_tag';
  $form[$key] = array(
    '#type' => 'select',
    '#required' => FALSE,
    '#title' => t('Pane separator tag'),
    '#default_value' => $settings[$key],
    '#empty_option' => t('- none -'),
    '#options' => array(
      'div' => '<div />',
      'span' => '<span />',
      'hr' => '<hr />',
    ),
  );

  $key = 'pane_separator_attributes_class';
  $form[$key] = array(
    '#type' => 'textfield',
    '#required' => FALSE,
    '#title' => t('Pane separator classes'),
    '#description' => t('Additional classes'),
    '#default_value' => $settings[$key],
  );

  return $form;
}

/**
 * Render contents of a panels layout region.
 *
 * @param array $vars
 *   - display: panels_display
 *   - owner_id: string - Owner of the region. Machine name of the panel or
 *     mini panel.
 *   - panes: string[] - Rendered panes.
 *   - settings: Custom settings of this style plugin.
 *   - region_id: string - Machine name of the region in the layout.
 *   - style: Info array of this style plugin.
 *
 * @return string
 *   Themed HTML string.
 *
 * @ingroup themeable
 */
function theme_$module$_$plugin_owner$_$plugin_type$_$plugin_name_short$_region_render($vars) {
  // This is just an example.
  $separator = '';
  if (!empty($vars['settings']['pane_separator_tag'])) {
    $tag = $vars['settings']['pane_separator_tag'];
    $attributes = array(
      'class' => array('pane-separator'),
    );
    if (!empty($vars['settings']['pane_separator_attributes_class'])) {
      $attributes['class'][] = $vars['settings']['pane_separator_attributes_class'];
    }

    $separator = '<' . $tag . drupal_attributes($attributes) . ($tag == 'hr' ? ' />' : '></' . $tag . '>');
  }

  return implode($separator, $vars['panes']);
}

/**
 * Settings form of "$module_human$: $plugin_name_short_human$" panel style in pane context.
 *
 * @param array $settings
 *   Style settings.
 * @param panels_display $panels_display
 *   Panels display object.
 * @param string $pane_id
 *   Name of the pane.
 * @param string $box_type
 *   In most cases this is equal to 'pane'.
 * @param array $form_state
 *   Form API form state array.
 *
 * @return array
 *   Form API render array.
 */
function $module$_$plugin_owner$_$plugin_type$_$plugin_name_short$_pane_settings_form($settings, $panels_display, $pane_id, $box_type, &$form_state) {
  // This is just an example.
  $key = 'title_tag';
  $form[$key] = array(
    '#type' => 'select',
    '#title' => t('HTML tag'),
    '#required' => TRUE,
    '#default_value' => $settings[$key],
    '#options' => array(
      'h1'   => t('Header 1'),
      'h2'   => t('Header 2'),
      'h3'   => t('Header 3'),
      'h4'   => t('Header 4'),
      'h5'   => t('Header 5'),
      'h6'   => t('Header 6'),
      'div'  => '<div />',
      'span' => '<span />',
    ),
  );

  $key = 'title_attributes_class';
  $form[$key] = array(
    '#type' => 'textfield',
    '#required' => FALSE,
    '#title' => t('Title classes'),
    '#description' => t('Additional classes to title tag.'),
    '#default_value' => $settings[$key],
  );

  return $form;
}

/**
 * Render a panels pane.
 *
 * @param array $vars
 *   - content: stdClass
 *   - pane: stdClass
 *   - display: panels_display
 *   - style: array
 *   - settings: array
 *
 * @return string|null
 *   Themed HTML string.
 * 
 * @ingroup themeable
 */
function theme_$module$_$plugin_owner$_$plugin_type$_$plugin_name_short$_pane_render($vars) {
  // This is just an example.
  if (empty($vars['content']->content)) {
    return NULL;
  }

  if (empty($vars['settings']['title_tag'])) {
    $vars['settings']['title_tag'] = 'h2';
  }

  $vars['title_tag'] = $vars['settings']['title_tag'];

  if (!empty($vars['settings']['title_attributes_class'])) {
    $vars['title_attributes_array']['class'][] = $vars['settings']['title_attributes_class'];
  }

  return theme('panels_pane', $vars);
}

