/**
 * Settings form callback.
 *
 * @param array $form
 *   Form API render array.
 * @param array $form_state
 *   Form API form state.
 * @param array|null $settings
 *   Settings of the current instance.
 *
 * @return array
 *   Form API render array.
 */
function $module$_$plugin_owner$_$plugin_type$_$plugin_name_short$_settings_form($form, &$form_state, $settings) {
  $key = 'odds';
  $form['settings'][$key] = array(
    '#type' => 'select',
    '#title' => t('Odds'),
    '#default_value' => $settings[$key],
    '#empty_option' => t('- Select -'),
    '#options' => drupal_map_assoc(range(1, 6)),
  );

  return $form;
}

