/**
 * @param panels_display $display
 * @param array $layout
 * @param array $settings
 */
function $module$_$plugin_owner$_$plugin_type$_$plugin_name$_settings_form($display, $layout, $settings) {
  $settings = drupal_array_merge_deep($layout['defaults'], $settings);

  $element = array(
    '#type' => 'fieldset',
    '#tree' => TRUE,
    '#title' => t('Layout settings'),
    '#collapsible' => TRUE,
    '#collapsed' => TRUE,
  );

  $regions = array('wrapper' => t('Wrapper')) + $layout['regions'];
  foreach ($regions as $region_name => $region_label) {
    $element[$region_name] = array(
      '#tree' => TRUE,
      '#type' => 'fieldset',
      '#title' => $region_label,
      '#collapsible' => TRUE,
      '#collapsed' => TRUE,
    );

    $element[$region_name]['attributes'] = array(
      '#tree' => TRUE,
      '#type' => 'fieldset',
      '#title' => t('Attributes'),
      '#collapsible' => TRUE,
      '#collapsed' => TRUE,
    );

    $element[$region_name]['attributes']['class'] = array(
      '#type' => 'textfield',
      '#required' => FALSE,
      '#title' => t('Class'),
      '#description' => t('Description'),
      '#default_value' => $settings[$region_name]['attributes']['class'],
    );
  }

  return $element;
}

