function $module$_$plugin_owner$_$plugin_type$_$plugin_name_short$_cache_get($settings, $display, $args, $contexts, $pane = NULL) {
  $cache_id = $module$_$plugin_owner$_$plugin_type$_$plugin_name_short$_cache_id($settings, $display, $args, $contexts, $pane);
  $cache = cache_get($cache_id, $settings['cache_bin']);
  if (!$cache) {
    return FALSE;
  }

  if ((REQUEST_TIME - $cache->created) > $conf['lifetime']) {
    return FALSE;
  }

  return $cache->data;

}

function $module$_$plugin_owner$_$plugin_type$_$plugin_name_short$_cache_set($settings, $content, $display, $args, $contexts, $pane = NULL) {
  $cache_id = $module$_$plugin_owner$_$plugin_type$_$plugin_name_short$_cache_id($settings, $display, $args, $contexts, $pane);
  cache_set($cache_id, $content);
}

function $module$_$plugin_owner$_$plugin_type$_$plugin_name_short$_cache_clear() {
}

