/**
 * Returns information $name_human$.
 *
 * @todo Write documentation in $module$.api.php file.
 *
 * @return array$END$
 */
function $module$_info_$name$() {
  $info =& drupal_static(__FUNCTION__);

  if ($info !== NULL) {
    return $info;
  }

  $cache_id = '$module$:info:$name$:' . $GLOBALS['language']->language;
  $cache = cache_get($cache_id);
  if (!empty($cache->data)) {
    $info = $cache->data;

    return $info;
  }

  module_load_include('inc', '$module$');
  $info = array();
  $hook = '$module$_$name$_info';
  foreach (module_implements($hook) as $module) {
    $function = "{$module}_{$hook}";
    $module_info_list = (array) $function();
    foreach ($module_info_list as $module_info_name => $module_info) {
      $info[$module_info_name] = $module_info + array(
        'module' => $module,
      );
    }
  }

  drupal_alter($hook, $info);
  cache_set($cache_id, $info);

  return $info;
}

