  );

  foreach ($$$cells_provider$ as $$$cell_key$ => $$$cell$) {
    $$$table$[$$$row_key$]['data'][$$$cell_key$] = array(
      'data' => $END$$$$cell$,
      // 'class' => array(),
    );
  }
}

