/**
 * Implements hook_entityreference_prepopulate_providers_info().
 */
function $module$_entityreference_prepopulate_providers_info() {
  $providers = array();

  $providers['$module$_$name$'] = array(
    'title' => t('$module_human$: $name_human$'),
    'description' => t('$description$'),
    'disabled' => $disabled$,
    'callback' => '$module$_entityreference_prepopulate_$name$',
  );

  return $providers;
}

/**
 * Entity reference prepopulate provider callback.
 */
function $module$_entityreference_prepopulate_$name$($field, $instance) {
  return array(rand(1, 10));
}

/**
 * Implements hook_field_attach_validate().
 */
function $module$_field_attach_validate($entity_type, $entity, &$errors) {
  list($id, $revision, $bundle_name) = entity_extract_ids($entity_type, $entity);
  if ($bundle_name) {
    $instances = field_info_instances($entity_type, $bundle_name);
    $lang_code = field_language($entity_type, $entity);
    foreach ($instances as $field_name => $instance) {
      $field = field_info_field($instance[$field_name]);
      if ($field['type'] === '?') {
        $items = field_get_items($entity_type, $entity, $field_name);
        if (is_array($items)) {
          foreach ($items as $delta => $item) {
            if (!empty($item['value']) && $item['value'] === 'invalid value') {
              $errors[$field_name][$lang_code][$delta][] = array(
                'error' => 'value',
                'message' => t('Useful error message'),
              );
            }
          }
        }
      }
    }
  }
}

