$command_specific['archive-dump'] = array(
  // Describe the archive contents.
  'description' => '$description$',
  // Add tags to the archive manifest. Delimit multiple by commas.
  'tags' => '$tags$',
  // The full path and filename in which the archive should be stored. If
  // omitted, it will be saved to the drush-backups directory and a filename
  // will be generated.
  'destination' => '$destination$',
  // Do not fail if the destination file exists; overwrite it instead.
  'overwrite' => '$overwrite$',
  // The generator name to store in the MANIFEST file. The default is "Drush
  // archive-dump".
  'generator' => '$generator$',
  // The generator version number to store in the MANIFEST file. The default is
  // 7.0-dev.
  'generatorversion' => '$generatorversion$',
  // Only print the destination of the archive. Useful for scripts that don't
  // pass --destination.
  'pipe' => '$pipe$',
  // Preserve symbolic links.
  'preserve-symlinks' => '$preserve_symlinks$',
  // Exclude Drupal core, so the backup only contains the site specific stuff.
  'no-core' => '$no_core$',
  // Options passed thru to the tar command.
  'tar-options' => '$tar_options$',
);

$END$//drushrc_command_specific_

$command_specific['archive-restore'] = array(
  // Specify where the Drupal site should be expanded, including the docroot.
  // Defaults to the current working directory.
  'destination' => '$destination$',
  // An optional table prefix to use during restore.
  'db-prefix' => '$db_prefix$',
  // A Drupal 6 style database URL indicating the target for database restore.
  // If not provided, the archived settings.php is used.
  'db-url' => '$db_url$',
  // Account to use when creating the new database. Optional.
  'db-su' => '$db_su$',
  // Password for the "db-su" account. Optional.
  'db-su-pw' => '$db_su_pw$',
  // Allow drush to overwrite any files in the destination.
  'overwrite' => '$overwrite$',
  // Options passed thru to the tar command.
  'tar-options' => '$tar_options$',
);

$END$//drushrc_command_specific_

