$command_specific['coder-review'] = array(
  // Show warnings at the specified level, one of major, minor, or critical.
  'severity' => '$severity$',
  // Show major and critical warnings.
  'major' => '$major$',
  // Show all warnings.
  'minor' => '$minor$',
  // Show only critical warnings.
  'critical' => '$critical$',
  // Run the specified review(s), "all" runs all reviews, also accepts a comma
  // separated list (no spaces).
  'reviews' => '$reviews$',
  // Run the comment review.
  'comment' => '$comment$',
  // Run the style review (default).
  'style' => '$style$',
  // Run the PHP CodeSniffer review.
  'sniffer' => '$sniffer$',
  // Run the i18n review.
  'i18n' => '$i18n$',
  // Run the security review.
  'security' => '$security$',
  // Run the SQL review.
  'sql' => '$sql$',
  // Run the 6.x -> 7.x upgrade review.
  'upgrade7x' => '$upgrade7x$',
  // Run the 7.x -> 8.x upgrade review.
  'upgrade8x' => '$upgrade8x$',
  // Run the druplart review.
  'druplart' => '$druplart$',
  // Run the production-ready review.
  'release' => '$release$',
  // Review all of core.
  'core' => '$core$',
  // Review all of contrib.
  'contrib' => '$contrib$',
  // Review all active modules and themes.
  'active' => '$active$',
  // Review all modules and themes (default).
  'all' => '$all$',
  // Review "default" modules and themes (as defined in coder settings).
  'default' => '$default$',
  // Display summary information only, no warning output.
  'summary' => '$summary$',
  // Hide results that are empty, opposite of --empty.
  'no-empty' => '$no_empty$',
  // Show or hide results that are empty, valid options are 0|no|off, on by
  // default.
  'empty' => '$empty$',
  // Enable or disable Ignore handling, valid options are 0|no|off, on by
  // default.
  'ignore' => '$ignore$',
  // Enable display of ignore "rule names", helpful when adding ignores.
  'ignorename' => '$ignorename$',
  // Output results as xml.
  'xml' => '$xml$',
  // Output results in Checkstyle xml format.
  'checkstyle' => '$checkstyle$',
  // Install this coder command as a .git pre-commit hook
  'git' => '$git$',
);

