$command_specific['core-rsync'] = array(
  // The unary flags to pass to rsync; --mode=rultz implies rsync -rultz. 
  // Default is -akz.
  'mode' => '$mode$',
  // Excludes settings.php from being rsynced.  Default.
  'exclude-conf' => '$exclude_conf$',
  // Allow settings.php to be rsynced. Default is to exclude settings.php.
  'include-conf' => '$include_conf$',
  // Include special version control directories (e.g. .svn).  Default is to
  // exclude vcs files.
  'include-vcs' => '$include_vcs$',
  // Exclude the files directory.
  'exclude-files' => '$exclude_files$',
  // Exclude all directories in "sites/" except for "sites/all".
  'exclude-sites' => '$exclude_sites$',
  // Exclude all directories in "sites/" except for "sites/all" and the site
  // directory for the site being synced.  Note: if the site directory is
  // different between the source and destination, use --exclude-sites followed
  // by "drush rsync @from:%site @to:%site"
  'exclude-other-sites' => '$exclude_other_sites$',
  // List of paths to exclude, seperated by : (Unix-based systems) or ;
  // (Windows).
  'exclude-paths' => '$exclude_paths$',
  // List of paths to include, seperated by : (Unix-based systems) or ;
  // (Windows).
  'include-paths' => '$include_paths$',
  // Replace {rsync-option-name} with the rsync option (or option='value') that
  // you would like to pass through to rsync. Examples include --delete,
  // --exclude=*.sql, --filter='merge /etc/rsync/default.rules', etc. See the
  // rsync documentation for a complete explaination of all the rsync options
  // and values.
  '{rsync-option-name}' => '${rsync_option_name}$',
);

$END$//drushrc_command_specific_

