$command_specific['make'] = array(
  // Print the make API version and exit.
  'version' => '$version$',
  // Set the number of concurrent projects that will be processed at the same
  // time. The default is 1.
  'concurrency' => '$concurrency$',
  // Specify a path under which modules and themes should be placed. Defaults to
  // sites/all for Drupal 6,7 and the corresponding directory in the Drupal root
  // for Drupal 8 and above.
  'contrib-destination' => '$contrib_destination$',
  // Force a complete build even if errors occur.
  'force-complete' => '$force_complete$',
  // Ignore md5 checksums for downloads.
  'ignore-checksums' => '$ignore_checksums$',
  // Output an md5 hash of the current build after completion. Use --md5=print
  // to print to stdout.
  'md5' => '$md5$',
  // The default location to load the XML update information from.
  'make-update-default-url' => '$make_update_default_url$',
  // Do not use the pm-download caching (defaults to cache enabled).
  'no-cache' => '$no_cache$',
  // Leave temporary build directories in place instead of cleaning up after
  // completion.
  'no-clean' => '$no_clean$',
  // Do not require a Drupal core project to be specified.
  'no-core' => '$no_core$',
  // Do not recurse into the makefiles of any downloaded projects; you can also
  // set [do_recursion] = 0 on a per-project basis in the makefile.
  'no-recursion' => '$no_recursion$',
  // Do not write a PATCHES.txt file in the directory of each patched project.
  'no-patch-txt' => '$no_patch_txt$',
  // Do not modify .info files when cloning from Git.
  'no-gitinfofile' => '$no_gitinfofile$',
  // Prepare the built site for installation. Generate a properly permissioned
  // settings.php and files directory.
  'prepare-install' => '$prepare_install$',
  // Generate a tar archive of the build. The output filename will be [build
  // path].tar.gz.
  'tar' => '$tar$',
  // Run a temporary test build and clean up.
  'test' => '$test$',
  // Retrieve translations for the specified comma-separated list of language(s)
  // if available for all projects.
  'translations' => '$translations$',
  // Preserves VCS directories, like .git, for projects downloaded using such
  // methods.
  'working-copy' => '$working_copy$',
  // How to download files. Should be autodetected, but this is an override if
  // it doesn't work. Options are "curl" and "make" (a native download method).
  'download-mechanism' => '$download_mechanism$',
  // Restrict the make to this comma-separated list of projects. To specify all
  // projects, pass *.
  'projects' => '$projects$',
  // Restrict the make to this comma-separated list of libraries. To specify all
  // libraries, pass *.
  'libraries' => '$libraries$',
  // Restrict the make options to this comma-separated list of options.
  'allow-override' => '$allow_override$',
  // The base URL which provides project release history in XML. Defaults to
  // http://updates.drupal.org/release-history.
  'source' => '$source$',
  // Work with development releases solely.
  'dev' => '$dev$',
);

