  // Backup all project files before updates.
  'version-control=backup' => '$version_control=backup$',
  // Quickly add/remove/commit your project changes to Bazaar.
  'version-control=bzr' => '$version_control=bzr$',
  // Quickly add/remove/commit your project changes to Subversion.
  'version-control=svn' => '$version_control=svn$',
  // Cache release XML and tarballs or git clones. Git clones use git's
  // --reference option.
  'cache' => '$cache$',
  // Download project packages using wget or curl.
  'package-handler=wget' => '$package_handler=wget$',
  // Use git.drupal.org to checkout and update projects.
  'package-handler=git_drupalorg' => '$package_handler=git_drupalorg$',
);

