  // A key in the $skip_tables array. @see example.drushrc.php. Optional.
  'skip-tables-key' => '$skip_tables_key$',
  // A comma-separated list of tables to exclude completely. Optional.
  'skip-tables-list' => '$skip_tables_list$',
  // A key in the $structure_tables array. @see example.drushrc.php. Optional.
  'structure-tables-key' => '$structure_tables_key$',
  // A comma-separated list of tables to include for structure, but not data.
  // Optional.
  'structure-tables-list' => '$structure_tables_list$',
  // A key in the $tables array. Optional.
  'tables-key' => '$tables_key$',
  // A comma-separated list of tables to transfer. Optional.
  'tables-list' => '$tables_list$',
  // Skip dump if result file exists and is less than "cache" hours old.
  // Optional; default is 24 hours.
  'cache' => '$cache$',
  // Do not cache the sql-dump file.
  'no-cache' => '$no_cache$',
  // Do not dump the sql database; always use an existing dump file.
  'no-dump' => '$no_dump$',
  // Database specification for source system to dump from.
  'source-db-url' => '$source_db_url$',
  // Override sql database port number in source-db-url. Optional.
  'source-remote-port' => '$source_remote_port$',
  // Remote machine to run sql-dump file on. Optional; default is local machine.
  'source-remote-host' => '$source_remote_host$',
  // Path to dump file. Optional; default is to create a temporary file.
  'source-dump' => '$source_dump$',
  // A key in the $db_url (D6) or $databases (D7+) array which provides the
  // data.
  'source-database' => '$source_database$',
  // A key within the SOURCE database identifying a particular server in the
  // database group.
  'source-target' => '$source_target$',
  // 
  'target-db-url' => '$target_db_url$',
  // 
  'target-remote-port' => '$target_remote_port$',
  // 
  'target-remote-host' => '$target_remote_host$',
  // 
  'target-dump' => '$target_dump$',
  // A key in the $db_url (D6) or $databases (D7+) array which shall receive the
  // data.
  'target-database' => '$target_database$',
  // Oy. A key within the TARGET database identifying a particular server in the
  // database group.
  'target-target' => '$target_target$',
  // Use a temporary file to hold dump files.  Implies --no-cache.
  'temp' => '$temp$',
  // Directory to store sql dump files in when --source-dump or --target-dump
  // are not used.
  'dump-dir' => '$dump_dir$',
  // Create a new database before importing the database dump on the target
  // machine.
  'create-db' => '$create_db$',
  // Account to use when creating a new database. Optional.
  'db-su' => '$db_su$',
  // Password for the "db-su" account. Optional.
  'db-su-pw' => '$db_su_pw$',
  // Do not pass --ordered-dump to sql-dump.  sql-sync orders the dumpfile by
  // default in order to increase the efficiency of rsync.
  'no-ordered-dump' => '$no_ordered_dump$',
  // Obscure email addresses and reset passwords in the user table post-sync.
  // Optional.
  'sanitize' => '$sanitize$',
);

