  // Run all available tests
  'all' => '$all$',
  // A comma delimited list of methods that should be run within the test class.
  // Defaults to all methods.
  'methods' => '$methods$',
  // Skip cleanup of temporary tables and files. Helpful for reading debug()
  // messages and other post-mortem forensics.
  'dirty' => '$dirty$',
  // Output verbose test results to a specified directory using the JUnit test
  // reporting format. Useful for integrating with Jenkins.
  'xml' => '$xml$',
  // Select output format. Available: table, csv, json, list, var_export, yaml.
  // Default is table.
  'format' => '$format$',
  // Fields to output.
  // group,class,name
  'fields' => '$fields$',
  // Specify how elements in a list should be separated. In lists of lists, this
  // applies to the elements in the inner lists.
  'list-separator' => '$list_separator$',
  // In nested lists of lists, specify how the outer lists ("lines") should be
  // separated.
  'line-separator' => '$line_separator$',
  // Add field labels before first line of data. Default is on; --field-labels=0
  // to disable.
  // Group,Class,Name
  'field-labels' => '$field_labels$',
);

$END$//drushrc_command_specific_

