$command_specific['watchdog-show'] = array(
  // The number of messages to show. Defaults to 10.
  'count' => '$count$',
  // Restrict to messages of a given severity level.
  'severity' => '$severity$',
  // Restrict to messages of a given type.
  'type' => '$type$',
  // Continuously show new watchdog messages until interrupted.
  'tail' => '$tail$',
  // To be used in conjunction with --tail. This is the number of seconds to
  // wait between each poll to the database. Delay is 1 second by default.
  'sleep-delay' => '$sleep_delay$',
  // Return extended information about each message.
  'extended' => '$extended$',
  // Select output format. Available: table, csv, json, list, var_export, yaml.
  // Default is table.
  'format' => '$format$',
  // Fields to output.
  // wid,type,message,severity,location,hostname,date,username
  'fields' => '$fields$',
  // Specify how elements in a list should be separated. In lists of lists, this
  // applies to the elements in the inner lists.
  'list-separator' => '$list_separator$',
  // In nested lists of lists, specify how the outer lists ("lines") should be
  // separated.
  'line-separator' => '$line_separator$',
  // Add field labels before first line of data. Default is on; --field-labels=0
  // to disable.
  // ID,Type,Message,Severity,Location,Hostname,Date,Username
  'field-labels' => '$field_labels$',
);

$END$//drushrc_command_specific_

$command_specific['webform-export'] = array(
  // The file path to export to (defaults to print to stdout)
  'file' => '$file$',
  // The exporter format to use. Out-of-the-box this may be "delimited" or
  // "excel".
  'format' => '$format$',
  // Delimiter between columns (defaults to site-wide setting). This option may
  // need to be wrapped in quotes. i.e. --delimter="\t".
  'delimiter' => '$delimiter$',
  // Comma-separated list of components IDs to include in the export. May also
  // include the built-in special options "serial", "sid", "time", "draft",
  // "ip_address", "uid", and "username".
  'components' => '$components$',
  // Integer 0 or 1 value. Set to 1 to print header components by their field
  // keys instead of labels.
  'header-keys' => '$header_keys$',
  // Integer 0 or 1 value. Set to 1 to print select list values by their field
  // keys instead of labels.
  'select-keys' => '$select_keys$',
  // Set to "separate" (default) or "compact" to determine how select list
  // values are exported.
  'select-format' => '$select_format$',
  // To export a specific range of submissions, specify one of the following
  // range types: "all", "new", "latest", or "range".
  'range-type' => '$range_type$',
  // Integer specifying the latest X submissions will be downloaded. Used if
  // "range-type" is "latest" or no other range options are provided.
  'range-latest' => '$range_latest$',
  // Alternative to "start" option. The submission ID at which to start
  // exporting. Used if "range-type" is "range" or no other range options are
  // provided.
  'range-start' => '$range_start$',
  // Alternative to "end" option. The submission ID at which to end exporting.
  // Used if "range-type" is "range" or no other range options are provided.
  'range-end' => '$range_end$',
  // The size of batches in rows (default 10000). If encoutering out of memory
  // errors, set this number lower to export fewer submissions per batch.
  'batch-size' => '$batch_size$',
  // Skip output of header rows (defaults to printing header)
  'skip-header' => '$skip_header$',
);

$END$//drushrc_command_specific_

$command_specific['zen'] = array(
  // A name for your theme.
  'name' => '$name$',
  // [a-z, 0-9] A machine-readable name for your theme.
  'machine-name' => '$machine_name$',
  // The path where your theme will be created. Defaults to: sites/all/themes
  'path' => '$path$',
  // A description of your theme.
  'description' => '$description$',
  // Remove all RTL stylesheets.
  'without-rtl' => '$without_rtl$',
);

$END$//drushrc_command_specific_
